{"changed":true,"filter":false,"title":"url_reset.py","tooltip":"/accounts/url_reset.py","value":"from django.conf.urls import url\nfrom django.core.urlresolvers import reverse_lazy\nfrom django.contrib.auth.views import password_reset, password_reset_done, password_reset_confirm, password_reset_complete\n\nurlpatterns = [\n    url(r'^$', password_reset,\n        {'post_reset_redirect': reverse_lazy('password_reset_done')}, name='password_reset'),\n    url(r'^done/$', password_reset_done, name='password_reset_done'),\n    url(r'^(?P<uidb64>[0-9A-Za-z]+)-(?P<token>.+)/$', password_reset_confirm,\n        {'post_reset_redirect': reverse_lazy('password_reset_complete')}, name='password_reset_confirm'),\n    url(r'^complete/$', password_reset_complete, name='password_reset_complete'),\n]","undoManager":{"mark":-2,"position":1,"stack":[[{"start":{"row":0,"column":32},"end":{"row":0,"column":33},"action":"insert","lines":["s"],"id":10}],[{"start":{"row":0,"column":32},"end":{"row":0,"column":33},"action":"remove","lines":["s"],"id":11}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":11,"column":1},"end":{"row":11,"column":1},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1571746618582}